import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

const MiniPost = () => {
    const { id } = useParams();
    const [post, setPost] = useState(null);

    useEffect(() => {
        const fetchPost = async () => {
            try {
                const response = await fetch(`/api/posts/${id}`);
                if (!response.ok) {
                    throw new Error('Failed to fetch post');
                }
                const data = await response.json();
                setPost(data);
            } catch (error) {
                console.error('Error fetching post:', error);
            }
        };

        fetchPost();
    }, [id]);

    if (!post) {
        return <div>Loading...</div>;
    }

    return (
        <article className="mini-post">
            <header>
                <h3><a href={`/post/${post.id}`}>{post.title}</a></h3>
                <time className="published" dateTime={post.published_at}>
                    {new Date(post.published_at).toLocaleDateString()}
                </time>
                <a href={`/category/${post.category_id}`}><img src={post.category_img_url} alt={post.category_name} /></a>
            </header>
            <a href={`/post/${post.id}`} className="image"><img src={post.image_url} alt={post.title} /></a>
        </article>
    );
};

export default MiniPost;
