import React, { useState, useEffect } from 'react';
import Header from './Header'; // Headerコンポーネントをインポート

const PostsList = () => {
    const [posts, setPosts] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [searchQuery, setSearchQuery] = useState(''); // 検索キーワードをstateで管理

    useEffect(() => {
        const fetchPosts = async () => {
            setLoading(true);
            setError(null);
            try {
                let url = `${process.env.REACT_APP_API_BASE_URL}/api/posts`;
                if (searchQuery) {
                    const encodedQuery = encodeURIComponent(searchQuery); // エンコード
                    url = `${process.env.REACT_APP_API_BASE_URL}/api/search?query=${encodedQuery}`; // URLを修正
                }
                const response = await fetch(url);
                console.log('API Request:', response);
                if (!response.ok) {
                    throw new Error('Failed to fetch posts');
                }
                const data = await response.json();
                console.log('API Response:', data);
                // レスポンスのデータ構造を確認し、必要に応じて修正
                if (Array.isArray(data)) {
                    setPosts(data);
                } else if (data && Array.isArray(data.posts)) {
                    setPosts(data.posts);
                } else {
                    throw new Error('Invalid API response format');
                }
            } catch (err) {
                console.error('Error fetching posts:', err);
                setError(err.message);
            } finally {
                setLoading(false);
            }
        };

        fetchPosts();
    }, [searchQuery]); // searchQueryが変更されたらAPIリクエストを再実行

    const handleSearch = (query) => {
        setSearchQuery(query); // 検索キーワードをstateに設定
    };

    if (loading) {
        return <div>Loading...</div>;
    }

    if (error) {
        return <div>Error: {error}</div>;
    }

    return (
        <div>
            <Header onSearch={handleSearch} /> {/* HeaderコンポーネントにonSearchをpropsとして渡す */}
            <section>
                <ul className="posts">
                    {posts.length === 0 && searchQuery && <li>検索結果はありません。</li>} {/* メッセージを追加 */}
                    {posts.map((post) => (
                        <li key={post.id}>
                            <article>
                                <header>
                                    <h3><a href={`/post/${post.id}`}>{post.title}</a></h3>
                                    <time className="published" dateTime={post.published_at}>
                                        {new Date(post.published_at).toLocaleDateString()}
                                    </time>
                                </header>
                                <a href={`/post/${post.id}`} className="image">
                                    <img src={post.image_url} alt={post.title} />
                                </a>
                            </article>
                        </li>
                    ))}
                </ul>
            </section>
        </div>
    );
};

export default PostsList;
