import React, { useState, useEffect } from 'react';
import Post from './Post';

const Main = () => {
    const [posts, setPosts] = useState([]);
    const [currentPage, setCurrentPage] = useState(1);
    const [totalPages, setTotalPages] = useState(1);
    const [loading, setLoading] = useState(true); // ローディング状態を追加
    const [error, setError] = useState(null); // エラー状態を追加

    useEffect(() => {
        const fetchPosts = async () => {
            setLoading(true); // ローディング開始
            setError(null); // エラーをリセット
            try {
                const response = await fetch(`${process.env.REACT_APP_API_BASE_URL}/api/posts?page=${currentPage}`);
                if (!response.ok) {
                    throw new Error('Failed to fetch posts');
                }
                const data = await response.json();
                setPosts(data.posts);
                setTotalPages(data.totalPages);
            } catch (err) {
                console.error('Error fetching posts:', err);
                setError(err.message); // エラーメッセージを設定
            } finally {
                setLoading(false); // ローディング終了
            }
        };

        fetchPosts();
    }, [currentPage]);

    const handlePreviousPage = () => {
        if (currentPage > 1) {
            setCurrentPage(currentPage - 1);
        }
    };

    const handleNextPage = () => {
        if (currentPage < totalPages) {
            setCurrentPage(currentPage + 1);
        }
    };

    if (loading) {
        return <div>Loading...</div>; // ローディングインジケータを表示
    }

    if (error) {
        return <div>Error: {error}</div>; // エラーメッセージを表示
    }

    return (
        <div id="main">
            <Post posts={posts} />
            <ul className="actions pagination">
                <li><a href="#" onClick={handlePreviousPage} className={`button large previous ${currentPage === 1 ? 'disabled' : ''}`}>前のページへ</a></li>
                <li><span>{currentPage} / {totalPages}</span></li> {/* 現在のページ番号と総ページ数を表示 */}
                <li><a href="#" onClick={handleNextPage} className={`button large next ${currentPage === totalPages ? 'disabled' : ''}`}>次のページへ</a></li>
            </ul>
        </div>
    );
};

export default Main;
