import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom'; // React Routerを使用する場合

const Post = () => {
    const { id } = useParams(); // React Routerを使用する場合
    const [post, setPost] = useState(null);

    useEffect(() => {
        const fetchPost = async () => {
            try {
                const response = await fetch(`/api/posts/${id}`); // APIエンドポイント
                if (!response.ok) {
                    throw new Error('Failed to fetch post');
                }
                const data = await response.json();
                setPost(data);
            } catch (error) {
                console.error('Error fetching post:', error);
            }
        };

        fetchPost();
    }, [id]); // idが変更されたときに再取得

    if (!post) {
        return <div>Loading...</div>;
    }

    return (
        <article className="post">
            <header>
                <div className="title">
                    <h2><a href={`/post/${post.id}`}>{post.title}</a></h2>
                    <p>{post.short_description || "Lorem ipsum dolor amet nullam consequat etiam feugiat"}</p>
                </div>
                <div className="meta">
                    <time className="published" dateTime={post.published_at}>
                        {new Date(post.published_at).toLocaleDateString()}
                    </time>
                    <a href={`/author/${post.author_id}`} className="author">
                        <span className="name">{post.author_name}</span>
                        <img src={post.author_img} alt={post.author_name} />
                    </a>
                </div>
            </header>
            <a href={`/post/${post.id}`} className="image featured">
                <img src={post.image_url} alt={post.title} />
            </a>
            <p>{post.content}</p>
            <footer>
                <ul className="actions">
                    <li><a href={`/post/${post.id}`} className="button large">Continue Reading</a></li>
                </ul>
                <ul className="stats">
                    <li><a href={`/category/${post.category_id}`}>General</a></li>
                    <li><a href={`/post/${post.id}/likes`} className="icon solid fa-heart">{post.likes || 28}</a></li>
                    <li><a href={`/post/${post.id}/comments`} className="icon solid fa-comment">{post.comments || 128}</a></li>
                </ul>
            </footer>
        </article>
    );
};

export default Post;
