import React, { useState, useEffect } from 'react';
import Post from './Post';

const Main = () => {
    const [posts, setPosts] = useState([]);
    const [currentPage, setCurrentPage] = useState(1);
    const [totalPages, setTotalPages] = useState(1);

    useEffect(() => {
        const fetchPosts = async () => {
            try {
                const response = await fetch(`${process.env.REACT_APP_API_BASE_URL}/api/posts?page=${currentPage}`);
                if (!response.ok) {
                    throw new Error('Failed to fetch posts');
                }
                const data = await response.json();
                setPosts(data.posts);
                setTotalPages(data.totalPages);
            } catch (error) {
                console.error('Error fetching posts:', error);
            }
        };

        fetchPosts();
    }, [currentPage]);

    const handlePreviousPage = () => {
        if (currentPage > 1) {
            setCurrentPage(currentPage - 1);
        }
    };

    const handleNextPage = () => {
        if (currentPage < totalPages) {
            setCurrentPage(currentPage + 1);
        }
    };

    return (
        <div id="main">
            <Post posts={posts} />
            <ul className="actions pagination">
                <li><a href="#" onClick={handlePreviousPage} className={`button large previous ${currentPage === 1 ? 'disabled' : ''}`}>前のページへ</a></li>
                <li><a href="#" onClick={handleNextPage} className={`button large next ${currentPage === totalPages ? 'disabled' : ''}`}>次のページへ</a></li>
            </ul>
        </div>
    );
};

export default Main;
