import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

const MiniPost = () => {
    const { id } = useParams();
    const [post, setPost] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    useEffect(() => {
        if (id) { // idが存在する場合のみAPIリクエストを実行
            const fetchPost = async () => {
                setLoading(true);
                setError(null);
                try {
                    const response = await fetch(`${process.env.REACT_APP_API_BASE_URL}/api/posts/${id}`);
                    if (!response.ok) {
                        throw new Error('Failed to fetch post');
                    }
                    const data = await response.json();
                    setPost(data);
                } catch (err) {
                    console.error('Error fetching post:', err);
                    setError(err.message);
                } finally {
                    setLoading(false);
                }
            };

            fetchPost();
        } else {
            setLoading(false);
            setError('Post ID is undefined');
        }
    }, [id]);

    if (loading) {
        return <div>Loading...</div>;
    }

    if (error) {
        return <div>Error: {error}</div>;
    }

    return (
        <article className="mini-post">
            <header>
                <h3><a href={`/post/${post.id}`}>{post.title}</a></h3>
                <time className="published" dateTime={post.published_at}>
                    {new Date(post.published_at).toLocaleDateString()}
                </time>
                <a href={`/category/${post.category_id}`}><img src={post.category_img_url} alt={post.category_name} /></a>
            </header>
            <a href={`/post/${post.id}`} className="image"><img src={post.image_url} alt={post.title} /></a>
        </article>
    );
};

export default MiniPost;
